<?phpclass DiagnosisControl extends ClinicalannotationAppModel {		/**	 * Get permissible values array gathering all existing diagnosis types.	 *	 * @author N. Luc	 * @since 2010-05-26	 * @updated N. Luc	 */  		function getDiagnosisTypePermissibleValuesFromId() {		$result = array();				// Build tmp array to sort according translation		foreach($this->find('all', array('conditions' => array('flag_active = 1'))) as $diagnosis_control) {			$result[$diagnosis_control['DiagnosisControl']['id']] = __($diagnosis_control['DiagnosisControl']['controls_type'], true);		}		asort($result);							return $result;	}			function getTypePermissibleValues() {		$result = array();				// Build tmp array to sort according translation		foreach($this->find('all', array('conditions' => array('flag_active = 1'))) as $diagnosis_control) {			$result[$diagnosis_control['DiagnosisControl']['controls_type']] = __($diagnosis_control['DiagnosisControl']['controls_type'], true);		}		asort($result);							return $result;	}			function getCategoryPermissibleValues() {		$result = array();				// Build tmp array to sort according translation		foreach($this->find('all', array('conditions' => array('flag_active = 1'))) as $diagnosis_control) {			$result[$diagnosis_control['DiagnosisControl']['category']] = __($diagnosis_control['DiagnosisControl']['category'], true);		}		asort($result);							return $result;	}}?>