<?phpclass DiagnosisControl extends ClinicalannotationAppModel {		/**	 * Get permissible values array gathering all existing diagnosis types.	 *	 * @return Array having following structure:	 * 	array ('value' => 'diagnosisControl.id', 'default' => (translated string describing diagnosis type))	 * 	 * @author N. Luc	 * @since 2010-05-26	 * @updated N. Luc	 */  		function getDiagnosisTypePermissibleValuesFromId() {		$result = array();		$tmp_result = array();				// Build tmp array to sort according translation		foreach($this->find('all', array('conditions' => array('flag_active = 1'))) as $diagnosis_control) {			$tmp_result[$diagnosis_control['DiagnosisControl']['id']] = __($diagnosis_control['DiagnosisControl']['controls_type'], true);		}		asort($tmp_result);							// Build final array		foreach($tmp_result as $value => $default) { $result[] = array('value' => $value, 'default' => $default); }				return $result;	}		}?>